/*
 *  int Process_Backslashes(void)
 *
 *  Covert any backslash \ escapes into the correct character.
 *  Operates on and modifies in place the value of zz_text; zz_leng
 *  is adjusted accordingly.  Returns the number of backslash
 *  substitutions made.  The following are supported:
 *      \a   (alert)
 *      \b   (backspace)
 *      \f   (formfeed)
 *      \n   (newline)
 *      \r   (carriage return)
 *      \t   (horizontal tab)
 *      \v   (vertical tab)
 *      \xhh (hexadecimal hh)              ***** NOT IMPLEMENTED *****
 *      \ooo (octal ooo where o == [0-7])  ***** NOT IMPLEMENTED *****
 *      \\n  (backslash before newline removes the backslash and newline)
 *      \.   (any other char produces that char)
 */
static int
Process_Backslashes(void)
{
  int old_index;
  int new_index;
  int substitution_count = 0;

  for (old_index=0, new_index=0; old_index<zz_leng; old_index++) {
    if (zz_text[old_index] != '\\') {
      zz_text[new_index++] = zz_text[old_index];
    } else {
      if (++old_index < zz_leng) {
        switch(zz_text[old_index]) {
        case 'a':
          zz_text[new_index++] = '\a';
          substitution_count++;
          break;
        case 'b':
          zz_text[new_index++] = '\b';
          substitution_count++;
          break;
        case 'f':
          zz_text[new_index++] = '\f';
          substitution_count++;
          break;
        case 'n':
          zz_text[new_index++] = '\n';
          substitution_count++;
          break;
        case 'r':
          zz_text[new_index++] = '\r';
          substitution_count++;
          break;
        case 't':
          zz_text[new_index++] = '\t';
          substitution_count++;
          break;
        case 'v':
          zz_text[new_index++] = '\v';
          substitution_count++;
          break;
        case 'x':
          /* need to add processing for
	   * hexadecimal numbers \xhh here
           */
          zz_text[new_index++] = zz_text[old_index];
          substitution_count++;
          break;
        case '0':
        case '1':
        case '2':
        case '3':
        case '4':
        case '5':
        case '6':
        case '7':
          /* need to add processing for
	   * octal numbers \ooo here
           */
          zz_text[new_index++] = zz_text[old_index];
          substitution_count++;
          break;
        case '\n':
	  /*  Backslash at the end of the line removes
	   *  the slash and the newline from the result,
	   *  so no futher processing is needed.
	   */
	  substitution_count++;
	  break;
        default:
          zz_text[new_index++] = zz_text[old_index];
          substitution_count++;
          break;
        }
      }
    }
  }
  zz_text[new_index] = '\0';
  zz_leng = new_index;
  return substitution_count;
}


